package com.angkorteam.mbaas.server.groovy

import com.angkorteam.mbaas.plain.response.GroovyResponse
import com.angkorteam.mbaas.server.bean.System
import org.springframework.beans.factory.annotation.Autowired
import org.springframework.http.ResponseEntity
import com.angkorteam.mbaas.server.spring.GroovyService

import javax.servlet.http.HttpServletRequest

/**
 * Created by socheat on 11/4/16.
 */
class SampleService implements GroovyService {

    @Autowired
    private System system;

    @Override
    ResponseEntity<GroovyResponse> service(HttpServletRequest request) throws Throwable {
        // enjoy your logic here
        GroovyResponse response = new GroovyResponse();
        response.setData(system.randomUUID());
        return ResponseEntity.ok(response);
    }

}
